    public static int majorityElementRevise(int[] a){
        int rank=0;
        int currele =a[0];
        for(int i =0; i<a.length;i++){
            if(a[i]==currele){
                rank++;
            }else{
                rank--;
            }
            if(rank<0){
                rank=1;
                currele=a[i];
            }
        }
        return currele;
    }

    public static int majorityElement(int[] nums) {
        int synergy = 0;
        int fp = 0, cp = 0, lz = 0;
        int length = nums.length;
        while (fp < length - 1) {
            int king = nums[fp];
            if (nums[cp++] == king) {
                synergy++;
            } else {
                synergy--;
            }
            if (synergy == 0) {
                lz = cp;
            }

            if (synergy == -(length / 2 - 1)) {
                fp = lz;
                cp = lz;
                synergy = 0;
            }

            if (cp >= nums.length - 1) {
                if (synergy > 0) {
                    break;
                } else {
                    fp = lz;
                    cp = lz;
                    synergy = 0;
                }
            }

        }
        return nums[fp];
    }
